    private double distanceTravelled; // in miles
    private double speed; // in mph

    public Car(String make, String model, int year, double fuelEfficiency) throws CarException {
        setMake(make);
        setModel(model);
        setYear(year);
        setFuelEfficiency(fuelEfficiency);
        this.distanceTravelled = 0;
        this.speed = 0;
    }

    // Getters and setters with validation
    public String getMake() { return make; }
    public void setMake(String make) { this.make = make; }

    public String getModel() { return model; }
    public void setModel(String model) { this.model = model; }

    public int getYear() { return year; }
    public void setYear(int year) throws CarException {
        int currentYear = java.time.Year.now().getValue();
        if (year < 1900 || year > currentYear) {
            throw new CarException("Year must be between 1900 and " + currentYear);
        }
        this.year = year;
    }

    public double getFuelEfficiency() { return fuelEfficiency; }
    public void setFuelEfficiency(double fuelEfficiency) throws CarException {
        if (fuelEfficiency <= 0) {
            throw new CarException("Fuel efficiency must be positive");
        }
        this.fuelEfficiency = fuelEfficiency;
    }

    public double getDistanceTravelled() { return distanceTravelled; }
    public void setDistanceTravelled(double distanceTravelled) throws CarException {
        if (distanceTravelled < 0) {
            throw new CarException("Distance travelled cannot be negative");
        }
        this.distanceTravelled = distanceTravelled;
    }

    public double getSpeed() { return speed; }

    // Car behaviors
    public void accelerate(double increment) {
        this.speed += increment;
    }

    public void decelerate(double decrement) {
        this.speed = Math.max(0, this.speed - decrement);
    }

    public void drive(double hours) {
        this.distanceTravelled += this.speed * hours;
    }

    public double calculateFuelConsumption() {
        return distanceTravelled / fuelEfficiency;
    }

    @Override
    public String toString() {
        return String.format("%d %s %s: %.1f mph, %.1f miles travelled", 
                year, make, model, speed, distanceTravelled);
    }

    // Custom exception class
    public static class CarException extends Exception {
        public CarException(String message) {
            super(message);
        }
    }

    // Main method to demonstrate functionality
    public static void main(String[] args) {
        try {
            Car myCar = new Car("Toyota", "Camry", 2020, 30.5);
            System.out.println("Created car: " + myCar);

            myCar.accelerate(60);
            myCar.drive(2); // Drive for 2 hours at 60 mph
