```java
import java.sql.*;
import java.util.Scanner;

public class EmployeeManagementSystem {
    private static final String DB_URL = "jdbc:mysql://localhost:3306/employeedb";
    private static final String USER = "root";
    private static final String PASSWORD = "password";

    public static void main(String[] args) {
        try (Connection connection = DriverManager.getConnection(DB_URL, USER, PASSWORD);
             Scanner scanner = new Scanner(System.in)) {

            while (true) {
                displayMenu();
                int choice = scanner.nextInt();
                scanner.nextLine(); // Consume newline

                switch (choice) {
                    case 1 -> addEmployee(connection, scanner);
                    case 2 -> updateEmployee(connection, scanner);
                    case 3 -> removeEmployee(connection, scanner);
                    case 4 -> System.exit(0);
                    default -> System.out.println("Invalid choice. Try again.");
                }
            }
        } catch (SQLException e) {
            System.err.println("Database error: " + e.getMessage());
        }
    }

    private static void displayMenu() {
        System.out.println("\nEmployee Management System");
        System.out.println("1. Add Employee");
        System.out.println("2. Update Employee");
        System.out.println("3. Remove Employee");
        System.out.println("4. Exit");
        System.out.print("Enter your choice: ");
    }

    private static void addEmployee(Connection connection, Scanner scanner) throws SQLException {
        System.out.print("Enter employee name: ");
        String name = scanner.nextLine();
        System.out.print("Enter employee position: ");
        String position = scanner.nextLine();
        System.out.print("Enter salary: ");
        double salary = scanner.nextDouble();

        String query = "INSERT INTO employees (name, position, salary) VALUES (?, ?, ?)";
        try (PreparedStatement statement = connection.prepareStatement(query)) {
            statement.setString(1, name);
            statement.setString(2, position);
            statement.setDouble(3, salary);
            statement.executeUpdate();
            System.out.println("Employee added successfully!");
        }
    }

    private static void updateEmployee(Connection connection, Scanner scanner) throws SQLException {
        System.out.print("Enter employee ID to update: ");
        int id = scanner.nextInt();
        scanner.nextLine(); // Consume newline

        System.out.print("Enter new position (or press enter to skip): ");
        String position = scanner.nextLine();
        System.out.print("Enter new salary (or 0 to skip): ");
        double salary = scanner.nextDouble();

        String query = "UPDATE employees SET " +
                       (position.isEmpty() ? "" : "position = ?, ") +
                       (salary > 0 ? "salary = ? " : "") +
                       "WHERE id = ?";

        try (PreparedStatement statement = connection.prepareStatement(query)) {
            int paramIndex = 1;
            if (!position.isEmpty()) {
                statement.setString(paramIndex++, position);
            }
            if (salary > 0) {
                statement.setDouble(paramIndex++, salary);
            }
            statement.setInt(paramIndex, id);

            int rowsUpdated = statement.executeUpdate();
            System.out.println(rowsUpdated > 0 ? "Employee updated successfully!" : "No employee found.");
        }
    }

    private static void removeEmployee(Connection connection, Scanner scanner) throws SQLException {
        System.out.print("Enter employee ID to remove: ");
        int id = scanner.nextInt();

        String query = "DELETE FROM employees WHERE id = ?";
        try (PreparedStatement statement = connection.prepareStatement(query)) {
            statement.setInt(1, id);
            int rowsDeleted = statement.executeUpdate();
            System.out.println(rowsDeleted > 0 ? "Employee removed successfully!" : "No employee found.");
        }
    }
}
