```python
import json

def process_people_data(json_string):
    # Parse the JSON string
    data = json.loads(json_string)

    # Initialize variables
    total_age = 0
    people_count = 0
    result = {"people": [], "statistics": {}}

    # Process each person
    for person in data["people"]:
        # Extract basic info
        name = person["name"]
        age = person["age"]

        # Extract education details
        education = person["details"]["education"]
        school = education["school"]
        major = education["major"]

        # Extract occupation details
        occupation = person["details"]["occupation"]
        current_job = occupation["current"]
        experience = occupation["experience"]

        # Add to total age for average calculation
        total_age += age
        people_count += 1

        # Create new structure for this person
        person_data = {
            "name": name,
            "education": {
                "school": school,
                "major": major
            },
            "occupation": {
                "title": current_job,
                "years_experience": experience
            }
        }

        result["people"].append(person_data)

    # Calculate average age
    if people_count > 0:
        result["statistics"]["average_age"] = total_age / people_count
    else:
        result["statistics"]["average_age"] = 0

    return json.dumps(result, indent=2)

# Example usage
