#include <iostream>
#include <string>

class Product {
private:
    std::string name;
    int quantity;

public:
    // Constructor to initialize product with name and quantity
    Product(const std::string& productName, int initialQuantity) 
        : name(productName), quantity(initialQuantity) {}

    // Method to add stock to the product
    void addStock(int amount) {
        if (amount > 0) {
            quantity += amount;
            std::cout << "Added " << amount << " units of " << name 
                      << ". New stock: " << quantity << std::endl;
        } else {
            std::cout << "Invalid amount to add. Amount must be positive." << std::endl;
        }
    }

    // Method to sell the product
    void sell(int amount) {
        if (amount <= 0) {
            std::cout << "Invalid amount to sell. Amount must be positive." << std::endl;
            return;
        }

        if (amount > quantity) {
            std::cout << "Cannot sell " << amount << " units of " << name 
                      << ". Insufficient stock. Available: " << quantity << std::endl;
        } else {
            quantity -= amount;
            std::cout << "Sold " << amount << " units of " << name 
                      << ". Remaining stock: " << quantity << std::endl;
        }
    }

    // Getter method for product name
    std::string getName() const {
        return name;
    }

    // Getter method for current quantity
    int getQuantity() const {
        return quantity;
    }
};

int main() {
    // Create some product instances
    Product laptop("Laptop", 10);
    Product phone("Smartphone", 20);
    Product headphones("Headphones", 15);

    // Demonstrate adding stock
    laptop.addStock(5);
    phone.addStock(10);
    headphones.addStock(3);

    // Demonstrate selling products
    laptop.sell(8);
    phone.sell(25);  // This should fail due to insufficient stock
    headphones.sell(5);

    // Try invalid operations
    laptop.addStock(-3);  // Invalid add
    phone.sell(0);        // Invalid sell

    return 0;
}
```

