#include <iostream>
#include <string>

using namespace std;

class Product {
private:
    string name;
    int quantity;

public:
    // Constructor to initialize product with name and quantity
    Product(string productName, int initialQuantity) : name(productName), quantity(initialQuantity) {}

    // Method to add stock to the product
    void addStock(int amount) {
        if (amount > 0) {
            quantity += amount;
            cout << "Added " << amount << " units of " << name << ". New stock: " << quantity << endl;
        } else {
            cout << "Invalid amount to add. Amount must be positive." << endl;
        }
    }

    // Method to sell product and update stock
    void sell(int amount) {
        if (amount <= 0) {
            cout << "Invalid amount to sell. Amount must be positive." << endl;
            return;
        }

        if (amount <= quantity) {
            quantity -= amount;
            cout << "Sold " << amount << " units of " << name << ". Remaining stock: " << quantity << endl;
        } else {
            cout << "Cannot sell " << amount << " units of " << name 
                 << ". Only " << quantity << " units available." << endl;
        }
    }

    // Getter method for product name
    string getName() const {
        return name;
    }

    // Getter method for current quantity
    int getQuantity() const {
        return quantity;
    }
};

int main() {
    // Create some product instances
    Product laptop("Laptop", 10);
    Product phone("Smartphone", 20);
    Product headphones("Headphones", 15);

    // Demonstrate adding stock
    laptop.addStock(5);
    phone.addStock(10);
    headphones.addStock(3);

    // Demonstrate selling products
    laptop.sell(8);
    phone.sell(25);  // This should fail due to insufficient stock
    headphones.sell(5);

    // Show final quantities
    cout << "\nFinal Inventory:" << endl;
    cout << laptop.getName() << ": " << laptop.getQuantity() << " units" << endl;
    cout << phone.getName() << ": " << phone.getQuantity() << " units" << endl;
    cout << headphones.getName() << ": " << headphones.getQuantity() << " units" << endl;

    return 0;
}
