#include <iostream>
#include <string>

class Product {
private:
    std::string name;      // The name of the product
    int quantity;          // The quantity of the product in stock

public:
    // Constructor to initialize the product with a name and quantity
    Product(const std::string& productName, int initialQuantity)
        : name(productName), quantity(initialQuantity) {}

    // Method to add stock to the product
    void addStock(int amount) {
        if (amount > 0) {
            quantity += amount;
            std::cout << amount << " units added to " << name << ". New quantity: " << quantity << std::endl;
        } else {
            std::cout << "Invalid amount. Please enter a positive number." << std::endl;
        }
    }

    // Method to sell stock from the product
    void sell(int amount) {
        if (amount > 0) {
            if (amount <= quantity) {
                quantity -= amount;
                std::cout << amount << " units sold from " << name << ". Remaining quantity: " << quantity << std::endl;
            } else {
