#endif // ADC_H

// adc.c
#include "adc.h"
#include <avr/io.h>

// Internal function to configure ADC registers
static void prv_configure_adc_registers(const AdcConfig* config) {
    // Configure reference voltage
    switch (config->reference_voltage) {
        case ADC_REF_EXTERNAL:
            ADMUX &= ~((1 << REFS1) | (1 << REFS0));
            break;
        case ADC_REF_INTERNAL_1V1:
            ADMUX = (ADMUX & ~(1 << REFS1)) | (1 << REFS0);
            break;
        case ADC_REF_INTERNAL_2V56:
            ADMUX |= (1 << REFS1) | (1 << REFS0);
            break;
    }

    // Configure resolution (by default, ADC uses right-adjusted 10-bit result)
    if (config->resolution == ADC_RESOLUTION_8BIT) {
        ADMUX |= (1 << ADLAR);  // Left-adjust result for 8-bit
    }

    // Enable ADC and set prescaler
    ADCSRA = (1 << ADEN) | (1 << ADPS2) | (1 << ADPS1) | (1 << ADPS0);
}

// Initialize ADC with given configuration
void adc_init(const AdcConfig* config) {
    if (config == NULL) {
        return;  // Error handling: invalid configuration
    }

    prv_configure_adc_registers(config);
}

// Read analog value from specified channel
uint16_t adc_read_channel(uint8_t channel) {
    // Validate channel number
    if (channel > 7) {
        return 0;  // Invalid channel
    }

    // Select ADC channel
    ADMUX = (ADMUX & 0xF0) | channel;

    // Start conversion
    ADCSRA |= (1 << ADSC);

    // Wait for conversion to complete
    while (ADCSRA & (1 << ADSC));

    // Return 10-bit result
    return ADC;
}

// Convert raw ADC value to voltage
float adc_convert_to_voltage(uint16_t raw_value, float reference_voltage) {
    return (raw_value * reference_voltage) / 1024.0;
}

// Get average reading from a channel
uint16_t adc_get_average_reading(uint8_t channel, uint8_t num_samples) {
    uint32_t total = 0;

    for (uint8_t i = 0; i < num_samples; i++) {
        total += adc_read_channel(channel);
    }

    return (uint16_t)(total / num_samples);
}
