#include <avr/io.h>

static AdcConfig currentConfig;

void adc_init(AdcConfig config) {
    currentConfig = config;

    // Set reference voltage
    ADMUX &= ~((1 << REFS1) | (1 << REFS0));
    switch(config.refVoltage) {
        case ADC_REF_VOLTAGE_VCC:
            ADMUX |= (1 << REFS0);
            break;
        case ADC_REF_VOLTAGE_INTERNAL:
            ADMUX |= (1 << REFS1) | (1 << REFS0);
            break;
        case ADC_REF_VOLTAGE_EXTERNAL:
            // REFS[1:0] = 00
            break;
    }

    // Set ADC resolution (ADLAR bit)
    if(config.resolution == ADC_RESOLUTION_8BIT) {
        ADMUX |= (1 << ADLAR);
    } else {
        ADMUX &= ~(1 << ADLAR);
    }

    // Set prescaler
    ADCSRA &= ~((1 << ADPS2) | (1 << ADPS1) | (1 << ADPS0));
    switch(config.prescaler) {
        case ADC_PRESCALER_2:
            ADCSRA |= (1 << ADPS0);
