            break;
        case ADC_PRESCALER_128:
            ADCSRA |= (1 << ADPS2) | (1 << ADPS1) | (1 << ADPS0);
            break;
    }

    // Enable ADC
    ADCSRA |= (1 << ADEN);
}

uint16_t adc_read_channel(uint8_t channel) {
