    // Select channel
    ADMUX = (ADMUX & 0xF0) | (channel & 0x0F);

    // Start conversion
    ADCSRA |= (1 << ADSC);

    // Wait for conversion to complete
    while(ADCSRA & (1 << ADSC));

    // Read result based on resolution
    if(currentConfig.resolution == ADC_RESOLUTION_8BIT) {
        return ADCH;
    } else {
        return ADC;
    }
}

uint16_t adc_get_ref_voltage_mv(void) {
    switch(currentConfig.refVoltage) {
