        case ADC_REF_VOLTAGE_INTERNAL: return 1100; // 1.1V internal reference
        case ADC_REF_VOLTAGE_EXTERNAL: return 3300; // Example external reference
        default: return 5000;
    }
}

uint16_t adc_raw_to_millivolts(uint16_t rawValue) {
    uint16_t maxValue = (currentConfig.resolution == ADC_RESOLUTION_8BIT) ? 255 : 1023;
    uint32_t voltage = (uint32_t)rawValue * adc_get_ref_voltage_mv();
    return (uint16_t)(voltage / maxValue);
}
