void adc_init(AdcConfig config) {
    // Set reference voltage
    ADMUX = (ADMUX & ~(0xC0)) | (config.reference << REFS0);

    // Set ADC prescaler
    ADCSRA = (ADCSRA & ~0x07) | config.prescaler;

    // Enable ADC
    ADCSRA |= (1 << ADEN);

    // Set resolution (assuming 10-bit is default)
    if (config.resolution == 8) {
        ADMUX |= (1 << ADLAR); // Left adjust result for 8-bit
    } else {
        ADMUX &= ~(1 << ADLAR); // Right adjust for 10-bit
    }
}

uint16_t adc_read_channel(uint8_t channel) {
    // Select ADC channel (0-7)
    ADMUX = (ADMUX & 0xF0) | (channel & 0x0F);

    // Start conversion
