    // Wait for conversion to complete
    while (ADCSRA & (1 << ADSC));

    // Return result
    if (ADMUX & (1 << ADLAR)) {
        return ADCH; // 8-bit result
    } else {
        return ADC;  // 10-bit result
    }
}

uint32_t adc_to_millivolts(uint16_t adc_value, uint32_t ref_millivolts) {
    if (ADMUX & (1 << ADLAR)) {
        // 8-bit resolution
        return (adc_value * ref_millivolts) / 256;
    } else {
        // 10-bit resolution
        return (adc_value * ref_millivolts) / 1024;
    }
}

uint16_t adc_read_average(uint8_t channel, uint8_t num_samples) {
    uint32_t sum = 0;
    for (uint8_t i = 0; i < num_samples; i++) {
        sum += adc_read_channel(channel);
    }
    return sum / num_samples;
}
