import java.io.IOException;
import java.nio.file.*;
import java.util.HashMap;
import java.util.Map;

public class IOUtils {
    /**
     * Retrieves all files within a specified directory and its subdirectories.
     * 
     * @param directoryPath The path of the directory to scan
     * @return A map where keys are relative paths and values are absolute paths
     * @throws IOException If an I/O error occurs while accessing the directory
     */
    public static Map<String, Path> getAllFiles(String directoryPath) throws IOException {
        Path rootPath = Paths.get(directoryPath).toAbsolutePath().normalize();
        Map<String, Path> fileMap = new HashMap<>();

        Files.walk(rootPath)
            .filter(Files::isRegularFile)
            .forEach(file -> {
                Path relativePath = rootPath.relativize(file);
                fileMap.put(relativePath.toString(), file.toAbsolutePath());
            });

        return fileMap;
    }
}
```

