public static Map<String, Path> getAllFiles(String directoryPath) {
    try {
        Path rootPath = Paths.get(directoryPath);

        // Validate input directory
        if (!Files.exists(rootPath) || !Files.isDirectory(rootPath)) {
            throw new IllegalArgumentException("Invalid directory path: " + directoryPath);
        }

        // Use Files.walk to traverse directory and its subdirectories
        return Files.walk(rootPath)
            .filter(Files::isRegularFile)  // Only include regular files
            .collect(Collectors.toMap(
                path -> rootPath.relativize(path).toString(),  // Relative path as key
                path -> path,  // Absolute path as value
                (v1, v2) -> v1  // Handle potential duplicate keys (keep first)
            ));
    } catch (IOException e) {
        // Handle potential IO exceptions during file traversal
        throw new UncheckedIOException("Error retrieving files from directory", e);
    }
}
