class RiskAssessment:
    VALID_RISK_CATEGORIES = {'credit', 'market', 'operational'}
    MIN_RISK_LEVEL = 1
    MAX_RISK_LEVEL = 5

    def assert_risk_values(self, risk):
        """
        Validates the risk values provided by clients.

        :param risk: A dictionary where keys are risk categories and values are risk levels.
        :raises ValueError: If any risk category is invalid or if any risk level is out of range.
        :return: True if all checks pass.
        """
        for category, level in risk.items():
            if category not in self.VALID_RISK_CATEGORIES:
                raise ValueError(f"Invalid risk category: '{category}'. Valid categories are {self.VALID_RISK_CATEGORIES}.")
            if not (self.MIN_RISK_LEVEL <= level <= self.MAX_RISK_LEVEL):
                raise ValueError(f"Risk level for '{category}' must be between {self.MIN_RISK_LEVEL} and {self.MAX_RISK_LEVEL}, but got {level}.")

        return True

# Example usage:
risk_assessment = RiskAssessment()
risk = {'credit': 3, 'market': 4, 'operational': 2}
assert risk_assessment.assert_risk_values(risk)
```

