            raise ValueError(f"Unsupported operation. Supported operations are: {cls.suboperators}")

        if operation == '+':
            return num1 + num2
        elif operation == '-':
            return num1 - num2
        elif operation == '*':
            return num1 * num2
        elif operation == '/':
            if num2 == 0:
                raise ZeroDivisionError("Division by zero is not allowed")
            return num1 / num2


