from typing import Union

def process_system_info(system_info: dict) -> Union[str, int]:
    """
    Processes the given system information to return the appropriate value based on the status.

    Args:
        system_info (dict): A dictionary containing system information including 'result',
                            'metadata' with 'status', and 'prometheus_alerts'.

    Returns:
        Union[str, int]: Returns the value of 'result' if 'metadata' status is 'success',
                         otherwise returns the count of 'prometheus_alerts'.
    """
