            return system_info['result']
        else:
            # Return the number of alerts if status is not 'success'
            return len(system_info['prometheus_alerts'])
    except KeyError as e:
        raise KeyError(f"Missing expected key in the input dictionary: {str(e)}")
    except Exception as e:
        raise Exception(f"An error occurred while processing system info: {str(e)}")

