class StringConversionManager:
    def __init__(self):
        self._conversions = []  # Initialize a list to hold conversions

    def _GetConversion(self, number):
        """Retrieve a conversion based on its number."""
        return self._conversions[number]

    def _MoveDown(self, selected_index):
        """Move the selected conversion down in the list."""
        try:
            # Retrieve the data of the selected conversion
            selected_conversion = self._GetConversion(selected_index)
            # Get the index of the next conversion
            next_index = selected_index + 1

            # Check if the next index is within bounds
            if next_index < len(self._conversions):
                # Swap the selected conversion with the next conversion
                self._conversions[selected_index], self._conversions[next_index] = (
                    self._conversions[next_index],
                    self._conversions[selected_index]
                )
            else:
                raise IndexError("No next conversion to swap with.")

        except IndexError as e:
            print(f"Error: {str(e)}")
