class SimpleOperation:
    def __init__(self, a, b):
        """
        Initialize the SimpleOperation class with two operands.

        :param a: First operand for the mathematical operation.
        :param b: Second operand for the mathematical operation.
        """
        self.a = a
        self.b = b

