```python
from functools import wraps
from flask import Flask, jsonify, request, make_response
import csv
import io

app = Flask(__name__)

# Dummy user session for demonstration
user_session = {
    'is_logged_in': True,
    'permissions': ['view_keyword_data']  # Example permission
}

# Decorator to check if the user is logged in
def login_required(f):
    @wraps(f)
    def decorated_function(*args, **kwargs):
        if not user_session.get('is_logged_in'):
            return jsonify({"error": "User must be logged in."}), 403
        return f(*args, **kwargs)
    return decorated_function

# Decorator to check if the user has access to the keyword data
def keyword_access_check(f):
    @wraps(f)
    def decorated_function(keyword, *args, **kwargs):
        if 'view_keyword_data' not in user_session.get('permissions', []):
            return jsonify({"error": "User does not have permission to access this keyword."}), 403
        return f(keyword, *args, **kwargs)
    return decorated_function

@app.route('/keyword_csv/<keyword>', methods=['GET'])
@login_required
@keyword_access_check
def keyword_csv(keyword):
    # Sample data for the CSV
    data = [
        {'keyword': keyword, 'response': 'Sample response 1'},
        {'keyword': keyword, 'response': 'Sample response 2'},
    ]

    # Create a CSV response
    output = io.StringIO()
    writer = csv.DictWriter(output, fieldnames=['keyword', 'response'])
    writer.writeheader()
    for row in data:
        writer.writerow(row)

    response = make_response(output.getvalue())
    response.headers["Content-Disposition"] = f"attachment; filename={keyword}_data.csv"
