#include <iostream>
#include <string>
#include <vector>
#include <stdexcept>
#include <numeric>

class StudentPortfolio {
private:
    std::string fullName; // Full legal name of the student
    std::string studentID; // Unique academic tracking identification number
    std::vector<double> grades; // List of grades for GPA calculation

public:
    // Constructor to initialize the student's portfolio
    StudentPortfolio(const std::string& name, const std::string& id)
        : fullName(name), studentID(id) {}

    // Method to add a grade
    void addGrade(double grade) {
        if (grade < 0.0 || grade > 4.0) { // Assuming a 4.0 GPA scale
            throw std::invalid_argument("Grade must be between 0.0 and 4.0");
        }
        grades.push_back(grade);
    }

    // Method to calculate GPA
    double calculateGPA() const {
        if (grades.empty()) {
            throw std::runtime_error("No grades available to calculate GPA");
        }
        double total = std::accumulate(grades.begin(), grades.end(), 0.0);
        return total / grades.size();
    }

    // Method to display student information
    void displayInfo() const {
        std::cout << "Student Name: " << fullName << std::endl;
        std::cout << "Student ID: " << studentID << std::endl;
        std::cout << "Current GPA: " << calculateGPA() << std::endl;
    }
};

