    double cumulativeGPA;

    // Private method to calculate GPA
    void calculateCumulativeGPA() {
        if (courseGrades.empty()) {
            cumulativeGPA = 0.0;
            return;
        }

        double totalPoints = 0.0;
        for (const auto& grade : courseGrades) {
            totalPoints += grade;
        }

        // Robust GPA calculation with error handling
        try {
            cumulativeGPA = totalPoints / courseGrades.size();
        } catch (const std::exception& e) {
            std::cerr << "GPA Calculation Error: " << e.what() << std::endl;
            cumulativeGPA = 0.0;
        }
    }

public:
