```python
def advanced_roman_numeral_conversion(value, conversion_type, include_subtractive, validate, format_output):
    """
    Exchange a positive integer or roman numeral as per the chosen conversion type.
    Conversion types: 'int_to_roman','roman_to_int' 
    Contains output notation feature: include_subtractive = True/False 
    Checks input data integrity comprehensively: validate = True/False 
    Renders user-friendly output: format_output = True/False 
    Limitations: 1 <= num <= 1,000,000 for integers; valid roman numerals only

    Example:
    >>> advanced_roman_numeral_conversion(4, 'int_to_roman', True, False, False) == 'iv'
    >>> advanced_roman_numeral_conversion(4, 'int_to_roman', False, False, False) == 'iiii'
    >>> advanced_roman_numeral_conversion('iv', 'roman_to_int', True, True, False) == 4
    >>> advanced_roman_numeral_conversion('iv','roman_to_int', True, True, True) == '(i)v'
    """

    # Roman numeral mapping
    roman_numerals = [
        (1000000, 'M'), (900000, 'CM'), (500000, 'D'), (400000, 'CD'),
        (100000, 'C'), (90000, 'XC'), (50000, 'L'), (40000, 'XL'),
        (10000, 'X'), (9000, 'MX'), (5000, 'V'), (4000, 'MV'),
        (1000, 'M'), (900, 'CM'), (500, 'D'), (400, 'CD'),
        (100, 'C'), (90, 'XC'), (50, 'L'), (40, 'XL'),
        (10, 'X'), (9, 'IX'), (5, 'V'), (4, 'IV'), (1, 'I')
    ]

    def int_to_roman(num):
        roman = ''
        for value, numeral in roman_numerals:
            while num >= value:
                roman += numeral
                num -= value
        return roman

    def roman_to_int(roman):
        roman_dict = {numeral: value for value, numeral in roman_numerals}
        total = 0
        prev_value = 0
        for char in reversed(roman):
            value = roman_dict[char]
            if value < prev_value:
                total -= value
            else:
                total += value
            prev_value = value
        return total

    def validate_input(value, conversion_type):
        if conversion_type == 'int_to_roman':
            if not isinstance(value, int) or not (1 <= value <= 1000000):
                raise ValueError(f"Invalid integer input: {value}. Must be between 1 and 1,000,000.")
        elif conversion_type == 'roman_to_int':
            if not isinstance(value, str) or not all(c in 'IVXLCDM' for c in value):
                raise ValueError(f"Invalid Roman numeral input: {value}.")

    def format_roman(roman):
        if format_output:
            parts = []
            for i in range(0, len(roman), 5):
                parts.append(f'({roman[i:i+5]})')
            return ''.join(parts)
        return roman

    if validate:
        validate_input(value, conversion_type)

    if conversion_type == 'int_to_roman':
        roman_result = int_to_roman(value)
        return format_roman(roman_result) if include_subtractive else roman_result.replace('IV', 'IIII').replace('IX', 'VIIII')

    elif conversion_type == 'roman_to_int':
        int_result = roman_to_int(value)
        return int_result

# Example usage
if __name__ == "__main__":
    print(advanced_roman_numeral_conversion(4, 'int_to_roman', True, False, False))  # Output: 'iv'
    print(advanced_roman_numeral_conversion(4, 'int_to_roman', False, False, False))  # Output: 'iiii'
