import java.util.concurrent.locks.ReentrantLock;

class Node {
    String identifier;
    int value;
    Node next;

    Node(String identifier, int value) {
        this.identifier = identifier;
        this.value = value;
        this.next = null;
    }
}

class CircularLinkedList {
    private Node tail;
    private final ReentrantLock lock = new ReentrantLock();

    // Insert a new node into the circular linked list
    public void insert(String identifier, int value) {
        lock.lock();
        try {
            Node newNode = new Node(identifier, value);
            if (tail == null) {
                tail = newNode;
                tail.next = tail; // Point to itself
            } else {
                newNode.next = tail.next; // Point to head
                tail.next = newNode;       // Current tail points to new node
                tail = newNode;            // Update tail to new node
            }
        } finally {
            lock.unlock();
        }
    }

    // Delete a node by identifier
    public boolean delete(String identifier) {
        lock.lock();
        try {
            if (tail == null) return false; // List is empty

            Node current = tail.next; // Start from head
            Node previous = tail;

            do {
                if (current.identifier.equals(identifier)) {
                    if (current == tail) {
                        if (tail == tail.next) { // Only one node in the list
                            tail = null;
                        } else {
                            previous.next = current.next; // Bypass the current node
                            tail = previous; // Update tail if necessary
                        }
                    } else {
                        previous.next = current.next; // Bypass the current node
                    }
                    return true; // Node deleted
                }
                previous = current;
                current = current.next;
            } while (current != tail.next); // Loop until we come back to head
            return false; // Node not found
        } finally {
            lock.unlock();
        }
    }

    // Search for a node by identifier
    public Node search(String identifier) {
        lock.lock();
        try {
            if (tail == null) return null; // List is empty

            Node current = tail.next; // Start from head
            do {
                if (current.identifier.equals(identifier)) {
                    return current; // Node found
                }
                current = current.next;
            } while (current != tail.next); // Loop until we come back to head
            return null; // Node not found
        } finally {
            lock.unlock();
        }
    }

    // Display the list (for testing purposes)
    public void display() {
        lock.lock();
        try {
            if (tail == null) return; // List is empty

            Node current = tail.next; // Start from head
            do {
                System.out.println("Identifier: " + current.identifier + ", Value: " + current.value);
                current = current.next;
            } while (current != tail.next); // Loop until we come back to head
        } finally {
            lock.unlock();
        }
    }
}

// Main class to demonstrate the CircularLinkedList functionality
public class CircularLinkedListExample {
    public static void main(String[] args) {
        CircularLinkedList list = new CircularLinkedList();
        list.insert("A", 1);
        list.insert("B", 2);
        list.insert("C", 3);

        System.out.println("Displaying list:");
        list.display();

        System.out.println("Searching for node with identifier 'B':");
        Node foundNode = list.search("B");
        if (foundNode != null) {
            System.out.println("Found: Identifier: " + foundNode.identifier + ", Value: " + foundNode.value);
        } else {
            System.out.println("Node not found.");
        }

        System.out.println("Deleting node with identifier 'B':");
        boolean deleted = list.delete("B");
        System.out.println(deleted ? "Node deleted." : "Node not found.");

        System.out.println("Displaying list after deletion:");
        list.display();
    }
}
```

