import java.util.concurrent.locks.ReentrantLock;

public class CircularLinkedList {
    private static class Node {
        final String id;
        final int value;
        Node next;
        final ReentrantLock lock = new ReentrantLock();

        Node(String id, int value) {
            this.id = id;
            this.value = value;
            this.next = this; // circular reference by default
        }

        void lock() {
            lock.lock();
        }

        void unlock() {
            lock.unlock();
        }
    }

    private Node head;
    private final ReentrantLock listLock = new ReentrantLock();
    private final int capacity;
    private int size;

    public CircularLinkedList(int capacity) {
        if (capacity <= 0) {
            throw new IllegalArgumentException("Capacity must be positive");
        }
        this.capacity = capacity;
        this.size = 0;
        this.head = null;
    }

    public boolean insert(String id, int value) {
        if (id == null) {
            throw new IllegalArgumentException("ID cannot be null");
        }

        listLock.lock();
        try {
            if (size >= capacity) {
                return false;
            }

            // Check for duplicate ID
            if (head != null) {
                Node current = head;
                do {
                    if (current.id.equals(id)) {
                        return false;
                    }
                    current = current.next;
                } while (current != head);
            }

            Node newNode = new Node(id, value);
            if (head == null) {
                head = newNode;
                head.next = head; // circular reference
            } else {
                Node tail = head;
                while (tail.next != head) {
                    tail = tail.next;
                }
                tail.next = newNode;
                newNode.next = head;
            }
            size++;
            return true;
        } finally {
            listLock.unlock();
        }
    }

    public boolean delete(String id) {
        if (id == null) {
            throw new IllegalArgumentException("ID cannot be null");
        }

        listLock.lock();
        try {
            if (head == null) {
                return false;
            }

            Node prev = null;
            Node current = head;
            boolean found = false;

            do {
                if (current.id.equals(id)) {
                    found = true;
                    break;
                }
                prev = current;
                current = current.next;
            } while (current != head);

            if (!found) {
                return false;
            }

            if (current == head) {
                if (head.next == head) { // only one node
                    head = null;
                } else {
                    Node tail = head;
                    while (tail.next != head) {
                        tail = tail.next;
                    }
                    tail.next = head.next;
                    head = head.next;
                }
            } else {
                prev.next = current.next;
            }
            size--;
            return true;
        } finally {
            listLock.unlock();
        }
    }

    public Integer search(String id) {
        if (id == null) {
            throw new IllegalArgumentException("ID cannot be null");
        }

        listLock.lock();
        try {
            if (head == null) {
                return null;
            }

            Node current = head;
            do {
                if (current.id.equals(id)) {
                    return current.value;
                }
                current = current.next;
            } while (current != head);

            return null;
        } finally {
            listLock.unlock();
        }
    }

    public int getSize() {
        listLock.lock();
        try {
            return size;
        } finally {
            listLock.unlock();
        }
    }

    public static void main(String[] args) {
        CircularLinkedList list = new CircularLinkedList(5);

        // Test insert
        System.out.println("Insert A: " + list.insert("A", 1)); // true
        System.out.println("Insert B: " + list.insert("B", 2)); // true
        System.out.println("Insert A again: " + list.insert("A", 3)); // false (duplicate)

        // Test search
        System.out.println("Search A: " + list.search("A")); // 1
        System.out.println("Search C: " + list.search("C")); // null

        // Test delete
        System.out.println("Delete B: " + list.delete("B")); // true
        System.out.println("Search B after delete: " + list.search("B")); // null
        System.out.println("Delete non-existent: " + list.delete("X")); // false

        // Test capacity
        System.out.println("Insert C: " + list.insert("C", 3)); // true
        System.out.println("Insert D: " + list.insert("D", 4)); // true
        System.out.println("Insert E: " + list.insert("E", 5)); // true
        System.out.println("Insert F: " + list.insert("F", 6)); // false (capacity reached)
    }
}
```

