```java
import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.List;

public class AnagramFinder {
    public static List<List<String>> findAnagrams(String[] lexemes) {
        HashMap<String, List<String>> anagramMap = new HashMap<>();

        for (String lexeme : lexemes) {
            if (lexeme.length() >= 2) {
                // Sort the characters in the lexeme
                char[] charArray = lexeme.toCharArray();
                Arrays.sort(charArray);
                String sortedLexeme = new String(charArray);

                // Group lexemes by their sorted character string
                anagramMap.computeIfAbsent(sortedLexeme, k -> new ArrayList<>()).add(lexeme);
            }
        }

        // Filter out groups that have more than one anagram
        List<List<String>> result = new ArrayList<>();
        for (List<String> anagrams : anagramMap.values()) {
            if (anagrams.size() > 1) {
                result.add(anagrams);
            }
        }

        return result;
    }

    public static void main(String[] args) {
        String[] lexemes = {"listen", "silent", "enlist", "hello", "world", "dell", "ledl"};
        List<List<String>> anagrams = findAnagrams(lexemes);

        for (List<String> group : anagrams) {
            System.out.println(group);
        }
    }
}
