    string second = lst[1];

    // Function to calculate the balance of parentheses
    auto calculate_balance = [](const string& s) {
        int balance = 0;
        for (char c : s) {
            if (c == '(') {
                balance++;
            } else {
                balance--;
            }
            // If balance goes negative, it means there are more ')' than '(' at some point
            if (balance < -1) return balance; // Too many closing parentheses
