    };

    // Check both concatenation orders
    int balance1 = calculate_balance(first + second);
    int balance2 = calculate_balance(second + first);

    // A good string requires the final balance to be zero
    if (balance1 == 0 || balance2 == 0) {
        return "Yes";
    } else {
        return "No";
    }
}

int main() {
    // Test cases
    cout << match_parens({"()(", ")"}) << endl; // Expected output: Yes
