        self._max_capacity = max_capacity
        self._animals = []

    def add_animal(self, animal):
        if len(self._animals) >= self._max_capacity:
            raise HabitatError("Habitat is at maximum capacity")

        if not isinstance(animal, Animal):
            raise HabitatError("Only Animal instances can be added")

        self._animals.append(animal)
        return f"Added {animal.get_info()} to {self._name}"

    def remove_animal(self, animal):
        if animal in self._animals:
            self._animals.remove(animal)
            return f"Removed animal from {self._name}"
        raise HabitatError("Animal not found in habitat")

    def count_animals(self):
        return len(self._animals)

