```java
import java.time.Year;

// Custom Exception Class
class CarException extends Exception {
    public CarException(String message) {
        super(message);
    }
}

// Car Class
class Car {
    // Attributes
    private String make;
    private String model;
    private int year;
    private double fuelEfficiency;
    private double distanceTravelled;
    private double currentSpeed;
    private double fuelLevel;

    // Constructor with validation
    public Car(String make, String model, int year, double fuelEfficiency) throws CarException {
        // Validate year
        if (year < 1900 || year > Year.now().getValue()) {
            throw new CarException("Invalid year. Year must be between 1900 and current year.");
        }

        // Validate fuel efficiency
        if (fuelEfficiency <= 0) {
            throw new CarException("Fuel efficiency must be a positive value.");
        }

        this.make = make;
        this.model = model;
        this.year = year;
        this.fuelEfficiency = fuelEfficiency;
        this.distanceTravelled = 0;
        this.currentSpeed = 0;
        this.fuelLevel = 0;
    }

    // Accelerate method
    public void accelerate(double speedIncrease) {
        currentSpeed += speedIncrease;
        System.out.println("Accelerating. Current speed: " + currentSpeed + " km/h");
    }

    // Decelerate method
    public void decelerate(double speedDecrease) {
        currentSpeed = Math.max(0, currentSpeed - speedDecrease);
        System.out.println("Decelerating. Current speed: " + currentSpeed + " km/h");
    }

    // Refuel method
    public void refuel(double fuelAmount) throws CarException {
        if (fuelAmount <= 0) {
            throw new CarException("Refuel amount must be positive.");
        }
        fuelLevel += fuelAmount;
        System.out.println("Refueled. Current fuel level: " + fuelLevel + " liters");
    }

    // Calculate Fuel Consumption method
    public double calculateFuelConsumption() throws CarException {
        if (distanceTravelled < 0) {
            throw new CarException("Distance travelled cannot be negative.");
        }

        double fuelConsumed = (distanceTravelled / 100) * fuelEfficiency;
        System.out.println("Fuel consumed: " + fuelConsumed + " liters");
        return fuelConsumed;
    }

    // Travel method to update distance
    public void travel(double distance) throws CarException {
        if (distance < 0) {
            throw new CarException("Travel distance cannot be negative.");
        }
        distanceTravelled += distance;
        System.out.println("Travelled " + distance + " km. Total distance: " + distanceTravelled + " km");
    }

    // Main method to demonstrate functionality
    public static void main(String[] args) {
        try {
            // Create a car
            Car myCar = new Car("Toyota", "Camry", 2022, 7.5);

            // Demonstrate methods
            myCar.accelerate(60);
            myCar.travel(100);
            myCar.refuel(50);
            myCar.calculateFuelConsumption();
            myCar.decelerate(30);

        } catch (CarException e) {
            // Handle custom exceptions
            System.err.println("Car Error: " + e.getMessage());
        }
    }
}
