/**
 * Swap two elements in a vector at specified indices.
 * 
 * @param numbers Vector to modify
 * @param index1 First index for swapping
 * @param index2 Second index for swapping
 */
void swapElements(std::vector<int>& numbers, int index1, int index2) {
    int temp = numbers[index1];
    numbers[index1] = numbers[index2];
    numbers[index2] = temp;
}

/**
 * Filter positive (non-negative) numbers and sort them in ascending order.
 * 
 * @param inputList Input vector of integers
 * @return Vector of non-negative numbers sorted in ascending order
 */
std::vector<int> getPositiveAndSort(const std::vector<int>& inputList) {
    // Filter non-negative numbers
    std::vector<int> nonNegative;
    for (int num : inputList) {
        if (num >= 0) {
            nonNegative.push_back(num);
        }
    }

    // Bubble sort for non-negative numbers
    for (size_t i = 0; i < nonNegative.size(); ++i) {
        for (size_t j = 0; j < nonNegative.size() - i - 1; ++j) {
            if (nonNegative[j] > nonNegative[j + 1]) {
                swapElements(nonNegative, j, j + 1);
            }
        }
    }

    return nonNegative;
}

/**
